package loops;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String fruits = "apple";
		switch (fruits)
		{
		case "apple":
			System.out.println("this is apple");
			break;
			
		case "orange":
			System.out.println("this is or");
			break;
		case "banana":
			System.out.println("this is baba");
			break;
			default:
			System.out.println("no matching");
		}
			
			
		
			
			
		
			
		
			
		}
		
		
		

	}

